package assisent;

public class Q6_2DArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
		int [][] num2d = {
		{15,-5},
		{7,99},
		{101,76}
		};

		//ROws = 3, Columns=2
		//index row = 0,1,2
		//index col= 0,1

		for(int r=0;r<=2;r++)
		{
		for(int c = 0; c<=1;c++)
		{
		System.out.println(num2d[r][c] + " ");

	}
		}
	}
}

	
